import React, { Component } from 'react';
import PropTypes from 'prop-types';

class TeamInMatch extends Component {
    url = "http://localhost:8080";

    constructor(props) {
        super(props)

        this.state = {
            teamNumber: 0,
            matchNumber: 0
        }
    }

    onSubmit = (e)=>{
        e.preventDefault();
        this.getMatch(3322, 1);
    };

    onChange = (e)=>{
        this.setState({[e.target.name]: e.target.value});
        console.log(this.state);
    }

    getMatch(teamNumber, matchNumber) {
        fetch(this.url + '/getteaminmatch/' + teamNumber +'/' + matchNumber, {
        }).then(doc=>doc.json()).then(doc => this.props.sendMatches(doc));
    }
    render() {
        return (
            <React.Fragment>
                <form onSubmit={this.onSubmit}>
                <input placeholder="Match Number" className="Entryfield" name="matchNumber" onChange={this.onChange}/>
                <input style={{'marginLeft': '1em'}} placeholder="Team Number" name="teamNumber" className="Entryfield"/>
                <button style={buttonStyle} type="submit">Search!</button>
                </form>
            </React.Fragment>
        )
    }
}

const buttonStyle = {
    backgroundColor: 'white',
    padding: '0.5em 1em',
    borderRadius: '0.5em'
}

TeamInMatch.propTypes = {
    sendMatches: PropTypes.func.isRequired
};

export default TeamInMatch;
