import React from 'react';
import ChildMatchComponent from "./ChildMatchComponent";
import {Col, Container, Row} from "react-bootstrap";
import TeamInMatch from './Searches/TeamInMatch';

class SearchComponent extends React.Component {
    url = "http://localhost:8080";
    constructor(props) {
        super(props);
        this.state = {matches: []};
    }

    setMatches = (matches) => {
        this.setState({matches: matches})
    }

    getMatch(teamNumber, matchNumber) {
        fetch(this.url + '/getteaminmatch/' + teamNumber +'/' + matchNumber, {
        }).then(doc=>doc.json()).then(doc => this.setState({matches: doc}));
    }

    componentDidMount() {
        this.getMatch(3322, 1);
    }

    render() {
        let matchComponents = [];
        for (let i = 0; i < this.state.matches.length; i++) {
            matchComponents.push(<ChildMatchComponent key={this.state.matches[i].matchNumber + "m_" + this.state.matches[i].robot.allianceColor + this.state.matches[i].robot.allianceNumber} match={this.state.matches[i]}> </ChildMatchComponent>);
        }
        return (
            <Container>
                <Row style={{'padding-bottom': '1em'}}>
                    <Col>
                        <TeamInMatch />
                    </Col>
                </Row>
                <Row>
                    <Col>
                        {matchComponents}
                    </Col>
                </Row>
            </Container>
        )

    }

}

export default SearchComponent
